import React, {useState, useEffect} from 'react';
import axios from "axios";

export default function TimeTable(){
    const[classes, setClasses] = useState([]);
    const[search, setsearch] = useState([]);
    const[clockState, setClockState] = useState();
    console.log(search)
    
    

    useEffect(()=>{
        function getClasses() {
            axios.get("http://localhost:8070/class/").then((res)=>{
               setClasses(res.data);
               console.log(res);
        }).catch((err)=>{
            alert(err.message);
        })
        }
        getClasses();

        setInterval(() => {
            const date = new Date();
            setClockState(date.toLocaleTimeString());
        }, 1000);
    
    } ) 



  
    
   
   


    return(
        
        <div className="container">
            <h1>Online Class Time Table</h1>
            <br></br>
            <div><span class="p-3 mb-5 border border-danger">{clockState}</span></div>
            <br></br>
            <br></br>
            <form class="d-flex">
            <input class="form-control me-2" type="text" onChange={(e) => setsearch(e.target.value)} placeholder="Search" aria-label="Search"/>
            </form>
            <br></br>
            
    
            <div>
            
            <table class="table table-borderless">
                <thead>
                    <tr>
                        <th scope="col">no</th>
                        <th scope="col">time</th>
                        <th scope="col">garde</th>
                        <th scope="col">class</th>
                        <th scope="col"></th>
                    </tr>
                </thead>
                <tbody>
                {classes.filter((classes) => {
                    return search.toString().toLowerCase() === '' ? classes: classes.grade.toString().toLowerCase().includes(search);
                    
                }).map((classes, index) => {
                    return(
                    <tr key={classes._id}>
                        <th scoop="row">{index+1}</th>
                        <td><div class="shadow p-3 mb-5 bg-danger body rounded border border-3 border border-dark text-white">{classes.time}</div></td> 
                        <td><div class="shadow p-3 mb-5 bg-danger body rounded border border-3 border border-dark text-white">{classes.grade}</div></td>
                        <td><div class="shadow p-3 mb-5 bg-danger body rounded border border-3 border border-dark text-white">
                           
                            {classes.classname}
                      
                        
                        </div></td>
                        <td>
                        <a className="p-3 mb-5 btn btn-success border border-3 border border-dark"  href={classes.link}>
                                <i className="fas fa-wifi"></i>&nbsp;Live
                            </a>
                        </td>
                        
                      
                    </tr>
                    )
                    
                
                
               
            })}

                </tbody>
            </table>

            </div>

            <br></br>

                

  
            

            
        </div>
      
        
    )

    
    
}
