import React, { Component } from 'react'
import axios from 'axios';

export default class GetClass extends Component {
  constructor(props){
    super(props);

    classId.state={
      get:{}
    };
  }

  componentDidMount(){
    const id = classId.props.match.params.id

    axios.get('/get/${id}').then((res) =>{
      if(res.data.success){
        this.setState({
          get:res.data.get
        });
      }
    });
  }
  render() {
    const{classname,grade} = this.state.get;
    return (
      <div style={{margin:'20px'}}>
        <h4>GetClass</h4>
        <hr/>

        <dl className="row">
          <dt className="col-sm-3">name</dt>
          <dd className="col-sm-9">{classname}</dd>

          <dt className="col-sm-3">grade</dt>
          <dd className="col-sm-9">{grade}</dd>

        </dl>
      </div>
    )
 
  }
}
