import React, {useState, useEffect} from 'react';
import axios from "axios";

export default function AllClasses(){
    const[classes, setClasses] = useState([]);
    

    useEffect(()=>{
        function getClasses() {
            axios.get("http://localhost:8070/class/").then((res)=>{
               setClasses(res.data);
               console.log(res);
        }).catch((err)=>{
            alert(err.message);
        })
        }
        getClasses();

        
    
    } ) 
  
    
   const deleteClass= async(id)=>{
    console.warn(id)
    let result = await fetch(`http://localhost:8070/class/delete/${id}`,{
        method:"Delete"
    });
    result = await result.json();
    if(result)
    {
        alert("Delete Successfully")
    }
   }


    return(
        <div className="container">
            <h1>All Classes</h1>
            <br></br>
            <div>
            <table class="table">
                <thead>
                    <tr>
                        <th scope="col">no</th>
                        <th scope="col">class</th>
                        <th scope="col">garde</th>
                        <th scope="col">date</th>
                        <th scope="col">time</th>
                        <th scope="col">link</th>
                        <th scope="col"></th>
                    </tr>
                </thead>
                <tbody>
                {classes.map((classes, index) => {
                    return(
                    <tr key={classes._id}>
                        <th scoop="row">{index+1}</th>
                        <td><a href={`/get/${classes._id}`} style={{textDecoration:'none'}}>{classes.classname} </a> </td> 
                        <td>{classes.grade}</td>
                        <td>{classes.date}</td>
                        <td>{classes.time}</td>
                        <td>{classes.link}</td>
                        <td>
                            <a className="btn btn-success" href={`/update/${classes._id}`}>
                                <i className="fas fa-edit"></i>&nbsp;Update
                            </a>

                            <h>    </h>
                            
                            <a className="btn btn-danger" href="#" onClick={()=>deleteClass(classes._id)}>
                                <i className="fas fa-trash-alt"></i>&nbsp;Delete
                            </a>
                            

                        </td>
                    </tr>
                    )
                    
                
                
               
            })}

                </tbody>
            </table>
            

            
        </div>
        </div>
    )

    
    
}
