import React, {useState, useEffect} from 'react';
import axios from "axios";

export default function GetClass(){
    const[id, setClass] = useState([]);
    

    useEffect(()=>{
        function getClass() {
            axios.get(`/get/${id}`).then((res)=>{
               setClass(res.data);
               console.log(res);
        }).catch((err)=>{
            alert(err.message);
        })
        }
        getClass();
    } ) 
    return(
        <div className="container">
            <h1>Class</h1>
            <br></br>
            <div>
            {id.map((classId, index) => {
    return (
      <div style={{margin:'20px'}}>
        <h4>GetClass</h4>
        <hr/>

        <dl className="row">
          <dt className="col-sm-3">name</dt>
          <dd className="col-sm-9">{classId.classname}</dd>

          <dt className="col-sm-3">grade</dt>
          <dd className="col-sm-9">{classId.grade}</dd>

        </dl>
      </div>
    )
  })}
            

            
        </div>
        </div>
    )
    
}
