import React, {useState, useEffect,useRef} from 'react';
import axios from "axios";

export default function TimeTable(){
    const conponentPDF = useRef();
    const[classes, setClasses] = useState([]);
    const[search, setsearch] = useState([]);
    console.log(search)
    
    

    useEffect(()=>{
        function getClasses() {
            axios.get("http://localhost:8070/class/").then((res)=>{
               setClasses(res.data);
               console.log(res);
        }).catch((err)=>{
            alert(err.message);
        })
        }
        getClasses();

        
    
    } ) 

    


  
    
   
   


    return(
        
        <div className="container">
            <h1>Online Class Time Table</h1>
            <br></br>
            <form class="d-flex">
            <input class="form-control me-2" type="text" onChange={(e) => setsearch(e.target.value)} placeholder="Search" aria-label="Search"/>
            </form>
            <br></br>
            <br></br>
            <br></br>
            <div>
            <div ref={conponentPDF} style={{width:'100%'}}>
            <table class="table table-borderless">
                <thead>
                    <tr>
                        <th scope="col">no</th>
                        <th scope="col">time</th>
                        <th scope="col">garde</th>
                        <th scope="col">class</th>
                        <th scope="col">link</th>
                        <th scope="col"></th>
                    </tr>
                </thead>
                <tbody>
                {classes.filter((classes) => {
                    return search.toString().toLowerCase() === '' ? classes: classes.grade.toString().toLowerCase().includes(search);
                    
                }).map((classes, index) => {
                    return(
                    <tr key={classes._id}>
                        <th scoop="row">{index+1}</th>
                        <td>{classes.time}</td> 
                        <td>{classes.grade}</td>
                        <td>{classes.classname}</td>
                        <td><a className="btn btn-success" href={classes.link}>
                                <i className="fas fa-edit"></i>&nbsp;Update
                            </a></td>
                        <td>
                            <a className="btn btn-success" href={`/update/${classes._id}`}>
                                <i className="fas fa-edit"></i>&nbsp;Update
                            </a>

                            
                            
                            
                            

                        </td>
                    </tr>
                    )
                    
                
                
               
            })}

                </tbody>
            </table>

            </div>

            <br></br>

                

  
            

            
        </div>
        </div>
        
    )

    
    
}
