import React, {useState} from "react";

export default function AddClass(){

  const [classname, setClassName] = useState("");
  const [grade, setGrade] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [link, setLink] = useState("");

  function sendData(e){
    e.preventDefault();
    
    const newClass ={
      classname,
      grade,
      date,
      time,
      link
    }
    console.log(newClass);
  }

  

    return(
        //class create form
        
        <div className="container">

          <br></br>
          <br></br>
          <h2>Create Class</h2>
          <br></br>
          <br></br>



        <div class="mb-3 row">
          <label for="classname" class="col-sm-2 col-form-label">Class</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="classname" placeholder="Class Name" 
          onChange={(e)=>{

            setClassName(e.target.value);

          }}/>
          </div>
        </div>

        <div class="mb-3 row">
          <label for="grade" class="col-sm-2 col-form-label">Grade</label>
          <div class="col-sm-10">
          <input type="text" class="form-control" id="grade" placeholder="Grade"
          onChange={(e)=>{

            setGrade(e.target.value);

          }}/>
          </div>
        </div>

        <div class="mb-3 row">
          <label for="date" class="col-sm-2 col-form-label">Date</label>
          <div class="col-sm-10">
          <input type="date" class="form-control" id="date" placeholder="Date"
          onChange={(e)=>{

            setDate(e.target.value);

          }}/>
          </div>
        </div>

        <div class="mb-3 row">
          <label for="time" class="col-sm-2 col-form-label">Time</label>
          <div class="col-sm-10">
          <input type="time" class="form-control" id="time" placeholder="Time"
          onChange={(e)=>{

            setTime(e.target.value);

          }}/>
          </div>
        </div>

        <div class="mb-3 row">
          <label for="link" class="col-sm-2 col-form-label">Link</label>
          <div class="col-sm-10">
          <input type="url" class="form-control" id="link" placeholder="Online Class Link"
          onChange={(e)=>{

            setLink(e.target.value);

          }}/>
          </div>
        </div>

        <button type="submit" class="btn btn-primary" onClick={sendData}>Submit</button>
      
        </div>
    )
}
